//AQUÍ IMPORTAMOS TODOS LOS COMPONENTES, DEPENDENCIAS Y ARCHIVOS CSS QUE UTILIZAREMOS
import './App.css';
import React from 'react';
import { CreateTodoButton } from './components/CreateTodoButton';
import { TodoCounter } from './components/TodoCounter';
import { TodoSearch } from './components/TodoSearch';
import { TodoList } from './components/TodoList';
import { TodoItem } from './components/TodoItem';
import { CompletedIcon } from './components/TodoIcon/CompleteIcon';

// ESTE ES NUESTRO CUSTOM HOOK QUE VA A TENER TODO A CERCA DEL MANEJO DE LOCAL-STORAGE
function useLocalStorage() {
  const [items, setItems] = React.useState();

  //AQUÍ ALMACENAMOS LOS TODOS QUE ESTÁN EN LOCAL-STORAGE
  const localStorageTodos = localStorage.getItem('TODOS_V1');

  let parsedTodos;

  if (!localStorageTodos) {
    localStorage.setItem('TODOS_V1', JSON.stringify([]));
    parsedTodos = [];
  } else {
    parsedTodos = JSON.parse(localStorageTodos);
  }

  //ESTA FUNCIÓN SE ENCARGA DE PERSISTIR Y GUARDAR LAS TAREAS EN EL ESTADO Y LOCALSTORAGE
  function saveItem(newItem) {
    localStorage.setItem('TODOS_V1', JSON.stringify(newItem));
    setItems(newTodos);
  }
}

function App() {
  //LISTA POR DEFECTO DE NUESTROS TODOS
  /*  const defaultTodos = [
     {
       text: 'Cortar cebolla',
       completed: true,
     },
     {
       text: 'Hacer curso de React',
       completed: false,
     },
     {
       text: 'Tomar agua',
       completed: false,
     },
     {
       text: 'Hacer ejercicios',
       completed: false,
     },
     {
       text: 'Comer tómates',
       completed: false,
     },
     {
       text: 'Comer naranjas',
       completed: false,
     },
   ];
  */

  //ESTADO DE LAS TAREAS
  const [todos, setTodos] = React.useState(parsedTodos);

  //ESTADO DE LAS BUSQUEDAS DE LAS TAREAS
  const [searchValue, setSearchValue] = React.useState("");

  //ESTA FUNCIÓN PERMITE MARCAR COMO COMPLETADO UNA TAREA
  function completeTodo(text) {
    const newTodos = [...todos];
    const todoIndex = newTodos.findIndex(todo => todo.text === text);
    newTodos[todoIndex].completed = true;
    saveTodos(newTodos);
  }

  //ESTA FUNCIÓN NOS PERMITE ELIMINAR UNA TAREA 
  function deleteTodo(text) {
    const newTodos = [...todos];
    const todoIndex = newTodos.findIndex(todo => todo.text === text);
    newTodos.splice(todoIndex, 1);
    saveTodos(newTodos);
  }

  //ESTA FUNCIÓN PERMITE ESCUCHAR EL EVENTO ONCHANGE PARA LAS TAREAS QUE TENGAN QUE SER BUSCADAS
  const searchedTodos = todos.filter(
    (todo) => {
      const todoText = todo.text.toLowerCase();
      const searchText = searchValue.toLowerCase();
      return todoText.includes(searchText);
    }
  )

  //AQUÍ GUARDAMOS LA LONGITUD DE NUESTRAS TAREAS
  const totalTodos = todos.length;

  //AQUÍ GUARDAMOS LA CANTIDAD DE TAREAS COMPLETADAS
  const completedTodos = todos.filter(todo => todo.completed === true).length;

  //AQUÍ RETORNAMOS NUESTROS ELEMENTOS (PODEMOS TENER COMPONENTES DENTRO DE OTROS)
  return (
    <>
      <TodoCounter
        completedTodos={completedTodos}
        totalTodos={totalTodos}
      />
      <TodoSearch
        setSearchValue={setSearchValue}
        searchValue={searchValue}
      />
      <TodoList>
        {searchedTodos.map(todo => (
          <TodoItem
            text={todo.text}
            completed={todo.completed}
            key={todo.text}
            onComplete={() => completeTodo(todo.text)}
            onDelete={() => deleteTodo(todo.text)}
          />
        ))}
      </TodoList>
      <CreateTodoButton />
    </>
  );
}

export default App;
