import './index.css';
import React from "react";
import { TodoContext } from '../TodoContext';

function TodoForm() {

   const { openModal, setOpenModal } = React.useContext(TodoContext);

   return (
      <div className='TodoFormContainer' onSubmit={handleForm}>
         <h4 className='TodoFormTitle'>Crea un nuevo TODO</h4>
         <form className="TodoForm">
            <input type="text" className="createTaskInput" placeholder="Escribe un nuevo TODO" />
            <button className="btn createTaskButton">Crear</button>
            <button className="btn cancelTaskButton" onClick={() => setOpenModal(openModal => !openModal)}>Cancelar</button>
         </form>
      </div>
   );
}

export { TodoForm };
