import './index.css';
import React from "react";
import { TodoContext } from '../TodoContext';
import { ErrorTodo } from '../ErrorTodo';


function TodoForm() {
   const { setOpenModal, addTodo, setCreateErrorTodo, CreateErrorTodo } = React.useContext(TodoContext);

   const [newTodoValue, setNewTodoValue] = React.useState("");

   const onSubmit = (e) => {
      e.preventDefault();
   }

   const onChange = (e) => {
      setNewTodoValue(e.target.value);
   }

   const onCreateTodo = () => {

      if (newTodoValue.length === 0) {
         setCreateErrorTodo(true);
      } else {
         addTodo(newTodoValue);
         setOpenModal(openModal => !openModal);
      }

   }

   return (
      <div className='TodoFormContainer' onSubmit={onSubmit}>
         <h4 className='TodoFormTitle'>Crea un nuevo TODO</h4>
         <form className="TodoForm">
            <input
               type="text"
               className="createTaskInput"
               placeholder="Escribe un nuevo TODO"
               value={newTodoValue}
               onChange={onChange}
            />
            <button
               className="btn createTaskButton"
               onClick={onCreateTodo}
            >
               Crear
            </button>
            <button
               className="btn cancelTaskButton"
               onClick={() => setOpenModal(openModal => !openModal)}>
               Cancelar
            </button>
         </form>
         {create}
      </div>
   );
}

export { TodoForm };
