import React from 'react';
import './index.css';

function TodoItem({ text, completed, onComplete, onDelete }) {
   const [check, setCheck] = React.useState(false);

   const handleCheck = () => {
      if (completed) setCheck(true);
      else setCheck(false);
   }

   return (
      <li className={`TodoItem ${completed && "TodoItem active"}`}>
         <div>
            <input
               type='checkbox'
               className='TodoItemCheckbox'
               onClick={onComplete}
               checked={completed}
            />
            <p className={`TodoItemText ${completed && "TodoItemText--active"}`}>{text}</p>
         </div>
         <span onClick={onDelete}>
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={3.6} stroke="currentColor" className="TodoItemDeleteIcon">
               <path strokeLinecap="round" strokeLinejoin="round" d="M6 18L18 6M6 6l12 12" />
            </svg>
         </span>
      </li >
   );
}

export { TodoItem };
