//AQUÍ IMPORTAMOS TODOS LOS COMPONENTES, DEPENDENCIAS Y ARCHIVOS CSS QUE UTILIZAREMOS
import './index.css';
import React from 'react';
import { useLocalStorage } from './useLocalStorage';
import { AppUI } from './AppUI';

function App() {
  //LISTA POR DEFECTO DE NUESTROS TODOS
  /*  const defaultTodos = [
     {
       text: 'Cortar cebolla',
       completed: true,
     },
     {
       text: 'Hacer curso de React',
       completed: false,
     },
     {
       text: 'Tomar agua',
       completed: false,
     },
     {
       text: 'Hacer ejercicios',
       completed: false,
     },
     {
       text: 'Comer tómates',
       completed: false,
     },
     {
       text: 'Comer naranjas',
       completed: false,
     },
   ];
  */

  //ESTADO DE LAS TAREAS
  const [todos, setTodos] = useLocalStorage('TODOS_V1', []);

  //ESTADO DE LAS BUSQUEDAS DE LAS TAREAS
  const [searchValue, setSearchValue] = React.useState("");

  //ESTA FUNCIÓN PERMITE MARCAR COMO COMPLETADO UNA TAREA
  function completeTodo(text) {
    const newTodos = [...todos];
    const todoIndex = newTodos.findIndex(todo => todo.text === text);
    newTodos[todoIndex].completed = true;
    setTodos(newTodos);
  }

  //ESTA FUNCIÓN NOS PERMITE ELIMINAR UNA TAREA 
  function deleteTodo(text) {
    const newTodos = [...todos];
    const todoIndex = newTodos.findIndex(todo => todo.text === text);
    newTodos.splice(todoIndex, 1);
    setTodos(newTodos);
  }

  console.log('1');
  console.log('2');
  console.log('3');

  //ESTA FUNCIÓN PERMITE ESCUCHAR EL EVENTO ONCHANGE PARA LAS TAREAS QUE TENGAN QUE SER BUSCADAS
  const searchedTodos = todos.filter(
    (todo) => {
      const todoText = todo.text.toLowerCase();
      const searchText = searchValue.toLowerCase();
      return todoText.includes(searchText);
    }
  )

  //AQUÍ GUARDAMOS LA LONGITUD DE NUESTRAS TAREAS
  const totalTodos = todos.length;

  //AQUÍ GUARDAMOS LA CANTIDAD DE TAREAS COMPLETADAS
  const completedTodos = todos.filter(todo => todo.completed === true).length;

  //AQUÍ RETORNAMOS NUESTROS ELEMENTOS (PODEMOS TENER COMPONENTES DENTRO DE OTROS)
  return (
    <AppUI
      setTodos={setTodos}
      todos={todos}
      completedTodos={completedTodos}
      totalTodos={totalTodos}
      searchedTodos={searchedTodos}
      deleteTodo={deleteTodo}
      completeTodo={completeTodo}
      searchValue={searchValue}
      setSearchValue={setSearchValue}
    />
  );
}

export default App;
