//AQUÍ IMPORTAMOS TODOS LOS COMPONENTES, DEPENDENCIAS Y ARCHIVOS CSS QUE UTILIZAREMOS
import './App.css';
import React from 'react';
import { CreateTodoButton } from './components/CreateTodoButton';
import { TodoMenu } from './components/TodoMenu';
import { TodoCounter } from './components/TodoCounter';
import { TodoSearch } from './components/TodoSearch';
import { TodoList } from './components/TodoList';
import { TodoItem } from './components/TodoItem';
import { ClearTodoButton } from './components/ClearTodoButton';

function App() {
  //LISTA POR DEFECTO DE NUESTROS TODOS
  const defaultTodos = [
    {
      text: 'Cortar cebolla',
      completed: true,
    },
    {
      text: 'Hacer curso de React',
      completed: false,
    },
    {
      text: 'Tomar agua',
      completed: false,
    },
    {
      text: 'Hacer ejercicios',
      completed: false,
    },
    {
      text: 'Comer tómates',
      completed: false,
    },
    {
      text: 'Comer naranjas',
      completed: false,
    },
  ];

  const [todos, setTodos] = React.useState(defaultTodos);

  //ESTA FUNCIÓN NOS PERMITE MOSTRAR LAS TAREAS EN CURSO (AÚN NO COMPLETADAS)
  function filterActivesTodos() {
    const newTodos = [...defaultTodos];
    const activesTodos = newTodos.map(todo => todo.key === false);
    setTodos(activesTodos);
  }

  //ESTA FUNCIÓN NOS PERMITE MOSTRAR LAS TAREAS COMPLETADAS
  function filterCompletedTodos() {
    const newTodos = todos.filter(todo => todo.completed);
    setTodos(newTodos);
  }

  //ESTA FUNCIÓN NOS PERMITE MOSTRAR TODAS LAS TAREAS
  function showAllTodos() {
    const newTodos = [...defaultTodos];
    const allTodos = newTodos.map(todo => todo);
    setTodos(allTodos);
  }

  //ESTA FUNCIÓN PERMITE MARCAR COMO COMPLETADO UNA TAREA
  function completeTodo(text) {
    const newTodos = [...defaultTodos];
    const todoIndex = newTodos.findIndex(todo => todo.text === text);
    newTodos[todoIndex].completed = true;
    setTodos(newTodos);
  }

  //ESTA FUNCIÓN NOS PERMITE ELIMINAR UNA TAREA 
  function deleteTodo(text) {
    const newTodos = [...defaultTodos];
    const todoIndex = newTodos.findIndex(todo => todo.text === text);
    newTodos.splice(todoIndex, 1);
    setTodos(newTodos);
  }

  //AQUÍ RETORNAMOS NUESTROS ELEMENTOS (PODEMOS TENER COMPONENTES DENTRO DE OTROS)
  return (
    <>
      <CreateTodoButton />
      <TodoMenu
        onFilterActivesTodos={filterActivesTodos}
        onFilterCompletedTodos={filterCompletedTodos}
        onShowAllTodos={showAllTodos}
      />
      <TodoList>
        {todos.map(todo => (
          <TodoItem
            text={todo.text}
            completed={todo.completed}
            key={todo.text}
            onComplete={() => completeTodo(todo.text)}
            onDelete={() => deleteTodo(todo.text)}
          />
        ))}
      </TodoList>
      <ClearTodoButton />
    </>
  );
}

export default App;
