import React from 'react';
import './index.css';

function TodoItem({ text, completed }) {
   const [isChecked, setIsChecked] = React.useState(false);
   const [classTodoItem, setClassTodoItem] = React.useState("TodoItem");

   console.log(isChecked)

   function checkHandler() {
      setIsChecked(!isChecked)
      isChecked ? setClassTodoItem("TodoItem") : setClassTodoItem("TodoItem active");
   }

   return (
      <li className={classTodoItem}>
         <div>
            <input 
               type='checkbox' 
               className='TodoItemCheckbox' 
               checked={isChecked} 
               onChange={checkHandler} 
            />
            <p className='TodoItemText'>{text}</p>
         </div>
         <span>
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={3.6} stroke="currentColor" className="TodoItemDeleteIcon">
               <path strokeLinecap="round" strokeLinejoin="round" d="M6 18L18 6M6 6l12 12" />
            </svg>
         </span>
      </li>
   );
}

export { TodoItem };
