import React from "react";
// ESTE ES NUESTRO CUSTOM HOOK QUE VA A TENER TODO A CERCA DEL MANEJO DE LOCAL-STORAGE
function useLocalStorage(itemName, initialValue) {
   //AQUÍ ALMACENAMOS LOS TODOS QUE ESTÁN EN LOCAL-STORAGE
   const localStorageItem = localStorage.getItem('TODOS_V1');

   let parsedItem;

   React.useEffect(() => {
      if (!localStorageItem) {
         localStorage.setItem(itemName, JSON.stringify(initialValue));
         parsedItem = initialValue;
      } else {
         parsedItem = JSON.parse(localStorageItem);
      }
   })

   const [item, setItem] = React.useState(parsedItem);

   //ESTA FUNCIÓN SE ENCARGA DE PERSISTIR Y GUARDAR LAS TAREAS EN EL ESTADO Y LOCALSTORAGE
   function saveItem(newItem) {
      localStorage.setItem('TODOS_V1', JSON.stringify(newItem));
      setItem(newItem);
   }

   return [item, saveItem];
}

export { useLocalStorage };
