import './index.css';
import React from "react";
import { FaTimes } from 'react-icons/fa';
import { FaCheck } from 'react-icons/fa';


function TodoIcon({ type, color }) {

   const iconTypes = {
      "delete": (color) => {
         <FaTimes className='Icon-svg' fill='color' />
      },
      "check": (color) => {
         <FaCheck className='Icon-svg ' fill='color' />
      }
   }

   return (
      <span className={`Icon-container Icon-conatiner-${type}`}>
         {iconTypes[type](color)}
      </span>
   );
}

export { TodoIcon }
